import React, { Component } from 'react';
import getWeb3 from '../ethereum/getWeb3';
import MyToken from "../ethereum/build/MyToken.json";
import MyTokenSale from "../ethereum/build/MyTokenSale.json";
import { Button, Form, Input } from 'semantic-ui-react';
import Layout from '../components/layout';



class Home extends Component {

  state = {
    web3: null,
    accounts: null,
    account: null,
    mytokenInstance: null,
    mytokenSaleInstance: null,
    tokenPrice: '1000000000000000',
    tokenBalance: 0,
    numberofToken: 0
  }

  componentDidMount = async () => {
    try {
      const web3 = await getWeb3();
      const accounts = await web3.eth.getAccounts();
      const networkId = await web3.eth.net.getId();

      const deployedNetwork1 = MyToken.networks[networkId];
      const deployedNetwork2 = MyTokenSale.networks[networkId];

      const mytokenInstance = new web3.eth.Contract(MyToken.abi,
        deployedNetwork1 && deployedNetwork1.address,
      );
      const mytokenSaleInstance = new web3.eth.Contract(MyTokenSale.abi,
        deployedNetwork2 && deployedNetwork2.address,
      );


      this.setState({ web3, accounts, mytokenInstance: mytokenInstance, mytokenSaleInstance: mytokenSaleInstance }, this.loadData);
    }
    catch (error) {
      console.log(error);
    }
  }

  loadData = async () => {
    try {
      const { mytokenInstance, mytokenSaleInstance, accounts, web3 } = this.state;
      let account = await web3.eth.getCoinbase();

      //debugger;
      //let tokenName = await mytokenInstance.methods.name().call();

      let tokenPrice = await mytokenSaleInstance.methods.tokenPrice().call();
      alert(tokenPrice);
      tokenPrice = web3.utils.fromWei(tokenPrice, 'ether');

      let tokenBalance = await mytokenInstance.methods.balanceOf(account).call();
      // alert(tokenPrice);
      //alert(tokenBalance);
      this.setState({ tokenPrice: tokenPrice, tokenBalance: tokenBalance, account });
    } catch (error) {
      alert(error);
      console.log(error);
    }
  }

  onSubmit = async () => {
    const tokenPrice = 1000000000000000
    const { mytokenSaleInstance, account } = this.state;
    await mytokenSaleInstance.methods.buyTokens(this.state.numberofToken).send({ from: account, value: tokenPrice * this.state.numberofToken });

  }

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <Layout>
        <div >
          <div >
            <h1 >My Token ICO SALE</h1>
            <hr />
            <br />
          </div>
          <div >
            <p>Introducing "My Token" (DAPP)! Token price is  {this.state.tokenPrice} Ether.
           You Currently have {this.state.tokenBalance} MyToken </p>
            <br />

            <Form onSubmit={this.onSubmit}>


              <Input focus
                value={this.state.numberofToken}
                onChange={event => this.setState({ numberofToken: event.target.value })}
              />

              <Button primary>Enter</Button>
            </Form>
            <br />
            <p>tokens sold</p>
            <hr />
            <p>Your Account: {this.state.account}</p>
          </div>
        </div>

      </Layout>
    );
  }
}

export default Home;
