import React, { Component } from 'react';
import getWeb3 from '../ethereum/getWeb3';
import MyToken from "../ethereum/build/MyToken.json";
import MyTokenSale from "../ethereum/build/MyTokenSale.json";
import { Button, Form, Input, Container } from 'semantic-ui-react';
import Head from 'next/head';


class Home extends Component {

  state = {
    web3: null,
    accounts: null,
    mytokenInstance: null,
    mytokenSaleInstance: null,
    tokenPrice: null,
    tokenBalance: null
  }

  componentDidMount = async () => {
    try {
      const web3 = await getWeb3();
      const accounts = await web3.eth.getAccounts();
      const networkId = await web3.eth.net.getId();

      const deployedNetwork = MyToken.networks[networkId];

      const mytokenInstance = new web3.eth.Contract(MyToken.abi,
        deployedNetwork && deployedNetwork.address,
      );
      const mytokenSaleInstance = new web3.eth.Contract(MyTokenSale.abi,
        deployedNetwork && deployedNetwork.address,
      );


      this.setState({ web3, accounts, mytokenInstance, mytokenSaleInstance }, this.loadData);
    }
    catch (error) {
      console.log(error);
    }
  }

  loadData = async () => {
    try {
      const mytokenInstance = this.state.mytokenInstance;
      const mytokenSaleInstance = this.state.mytokenSaleInstance;
      console.log(mytokenInstance.address);
      console.log(myt.address);
      let tokenPrice = await mytokenSaleInstance.methods.tokenPrice();
      console.log('my token price is ' + tokenPrice);
      //mytokenInstance.methods.balanceOf()
      this.setState({ tokenPrice: tokenPrice });
    } catch (error) {
      console.log(error);
    }
  }

  render() {
    return (
      <div>
        <h1>My Token ICO SALE</h1>
        <h2>{this.state.tokenPrice}</h2>
      </div>
    );
  }
}

export default Home;
